
public class CalculatorImpl implements Calculator{

	@Override
	public int add(int x, int y) {
		// TODO Auto-generated method stub
		return x+y;
	}

	@Override
	public int sub(int x, int y) {
		// TODO Auto-generated method stub
		return x-y;
	}

	@Override
	public int division(int x, int y) {
		// TODO Auto-generated method stub
		return x/y;
	}

	@Override
	public int multiply(int x, int y) {
		// TODO Auto-generated method stub
		return x*y;
	}

}
